test_that(".spacesToZero() works", {

  kwb.logger:::.spacesToZero(x = 1)
   kwb.logger:::.spacesToZero(x = 1:2)
   kwb.logger:::.spacesToZero(x = "a")
   kwb.logger:::.spacesToZero(x = c("a", "b"))
   kwb.logger:::.spacesToZero(x = TRUE)
   kwb.logger:::.spacesToZero(x = FALSE)
   kwb.logger:::.spacesToZero(x = as.POSIXct("2018-06-03 23:50:00"))
   kwb.logger:::.spacesToZero(x = list(key = c("a", "b"), value = 1:2))

})

